mapper.select:
select a.empee_acct from t_wk_app_token_info a 
where a.APP_TOKEN = #{appToken} and (a.is_valid = 1 and a.EXPIRE_TIME > sysdate)
and a.APP_CODE = #{appCode}

select id, install_files, client_platform, client_version_no, ver_disc from( select a.id, 'https://it.jisu8.cn:9999/hfile/dget.file?id='||a.install_files install_files, a.client_platform, a.client_version_no, a.ver_disc, row_number()over(partition by a.client_platform order by a.client_version_no desc) rm from t_wk_app_client_version a where a.client_id = '98237446-29fe-471c-a369-d519cd4c7df3'
and a.client_platform = #{clientPlatform}
and a.client_state = 1) where rm = 1
